<script type="text/javascript">
    function bc_lazyload(){
        $(".bc_lazyload:not(.lazyload)").each(function() {
            $(this).addClass('lazyload');
        });
    }
    $(window).on("load",function(){
        if (window.location.pathname != "/login" && window.location.pathname != "/vn/login") {
            Cookies.set('pageUrl', window.location.href, { domain: '.shopeeanalytics.com', expires: 1, path: '/' });
        }
        bc_lazyload();
    });
</script>
<script type="text/javascript">
    function surveySeller(){
        Swal.fire({
          title: 'Bạn là',
          html: 'Mục đích nâng cao chất lượng công cụ và tính năng',
          showCloseButton: true,
          showCancelButton: true,
          confirmButtonColor: '#3085d6',
          cancelButtonColor: '#d33',
          confirmButtonText: '<i class="fas fa-store-alt"></i> Người Bán',
          cancelButtonText:'<i class="fas fa-shopping-cart"></i> Người Mua',
        }).then((result) => {
            var isSeller = -1;
            if (result.dismiss && (result.dismiss == 'close' || result.dismiss == 'backdrop')){
                localStorage.setItem('survey_seller', '-1');
                return false;
            }
            if (result.value) {
                isSeller = 1;
            } else if (result.dismiss && (result.dismiss == 'cancel')){
                isSeller = 0;
            }
            if (isSeller != -1){
                localStorage.setItem('survey_seller', '1');
            }
            let token = $('body').data("token");
            $.ajax({
                type: 'POST',
                dataType: 'JSON',
                url: '/api/survey/',
                data: {
                    _saToken: token,
                    tp: 'survey',
                    survey_action: 'is_seller',
                    is_seller: isSeller
                },
                success: function(result) {
                    console.log(result);
                }
            });
        });
    }
    
    $(document).on('theme:init', function() {
        if (window.location.pathname != '/ma-giam-gia.html'){
            /*if (localStorage.getItem('survey_seller') !== '1') surveySeller();*/
        }
    });
</script>


